mapper.select:
select area_id
from area_vehicle
where vehicle_id = #{id,jdbcType=INTEGER}

select id
from vehicle
where area_updated_at >= #{updateTime,jdbcType=TIMESTAMP}

select t.*
from area t

    and t.id = #{id}
    and t.deleted = #{deleted}
    and t.geom_text = #{geomText}
    and t.geom_type = #{geomType}
    and t.limit_in_out = #{limitInOut}
    and t.limit_speed = #{limitSpeed}
    and t.mark_type = #{markType}
    and t.name = #{name}
    and t.created_at = #{createdAt}

mapper.update:
update area
name = #{name,jdbcType=VARCHAR}, area_desc = #{areaDesc,jdbcType=VARCHAR}, geom_type = #{geomType,jdbcType=TINYINT}, mark_type = #{markType,jdbcType=TINYINT}, limit_in_out = #{limitInOut,jdbcType=TINYINT}, limit_speed = #{limitSpeed,jdbcType=TINYINT}, limit_time = #{limitTime,jdbcType=TINYINT}, start_time = #{startTime,jdbcType=TIME}, end_time = #{endTime,jdbcType=TIME}, deleted = #{deleted,jdbcType=BIT}, geom_text = #{geomText,jdbcType=LONGVARCHAR},
where id = #{id,jdbcType=SMALLINT}
